from ascon_pcsn import *


KEY =   bytes.fromhex('4C8A55114D1CB6A9A2BE263D4D7AECAAFF')
NONCE = bytes.fromhex('4F4ED0EC0B98C529B7C8CDDF37BCD0284A')
DA =    bytes.fromhex('424120746F20428000')
WAVE =  bytes.fromhex('')

print("\nwave: ", WAVE[1:], "\n")

cipher = ascon_encrypt(KEY[1:], NONCE[1:], DA[1:], WAVE[1:], variant="Ascon-80pq")
print("cipher: ", cipher, "\n")

plain = ascon_decrypt(KEY[1:], NONCE[1:], DA[1:], cipher, variant="Ascon-80pq")
print("plain: ", plain, "\n")
